/* Function  structure for function  `calculadora` */

/*!50003 DROP FUNCTION IF EXISTS `calculadora` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `calculadora`(operador int, num1 float, num2 float) RETURNS decimal(10,2)
begin
declare resultado decimal(10,2);
case 
when operador = 1 then set resultado = num1 + num2;
when operador = 2 then set resultado = num1 - num2;
when operador = 3 then set resultado = num1 * num2;
when operador = 4 then set resultado = num1 / num2;
end case; 
RETURN resultado;
end */$$
DELIMITER ;

